import React, { useState, useEffect } from 'react';
import axiosInstance from '../AxiosInstance';
import SidebarLayout from '../SidebarLayout';
import VenueSearchForm from './VenueSearchForm';
import VenueTable from './VenueTable';
import VenuePagination from './VenuePagination';
import VenueDialog from './VenueDialog';
import { Container, Button } from '@mui/material';
import { useNotification } from '../NotificationContext';

const VenueMain = () => {
  const [timeZone, setTimeZone] = useState('');
  const [state, setState] = useState('');
  const [instructor, setInstructor] = useState('');
  const [venues, setVenues] = useState([]);
  const [schedules, setSchedules] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(2);
  const [currentVenue, setCurrentVenue] = useState({});
  const [open, setOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [isEditMode, setIsEditMode] = useState(false);
  const timeZones = ['PST', 'EST', 'CST', 'MST', 'GMT', 'UTC', 'BST', 'CEST'];
  const { showNotification } = useNotification();

  useEffect(() => {
    fetchVenues();
    // fetchSchedules();
  }, [currentPage]); // 当 currentPage 改变时重新获取数据

  const fetchVenues = async () => {
    try {
      const response = await axiosInstance.get('/venue', {
        params: {
          page: currentPage, // 修改：添加分页参数
          pageSize: itemsPerPage, // 修改：添加分页参数
        },
      });
      if (response.data.message === 'success') {
        setVenues(response.data.data.items);
      } else {
        showNotification('获取场地信息失败！', 'error');
      }
    } catch (error) {
      console.error('获取场地信息时出错:', error);
    }
  };

  const handleSearch = () => {
    if (isLoading) return;
    setIsLoading(true);

    let params = {
      pageNum: currentPage,
      pageSize: itemsPerPage,
      state,
      city: '',
      instructor: instructor,
      paymentMode: '',
      timeZone: timeZone,
    };

    Object.keys(params).forEach((key) => {
      if (params[key] === '' || params[key] === null || params[key] === undefined) {
        delete params[key];
      }
    });

    axiosInstance
      .get('/venue', { params })
      .then((response) => {
        if (response.data.message === 'success') {
          setVenues(response.data.data.items);
          showNotification('场地查找成功！', 'success');
        } else {
          showNotification('场地查找失败！', 'error');
        }
      })
      .catch((error) => {
        console.error('请求出错:', error);
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const handleAdd = () => {
    const newVenue = {
      id: null,
      state: '',
      city: '',
      address: '',
      timeZone: '',
      cancellationPolicy: '',
      paymentMode: '',
      nonrefundableFee: 0,
      fobKey: '',
      deposit: '',
      membershipFee: 0,
      usageFee: 0,
      refundableStatus: '',
      bookMethod: '',
      registrationLink: '',
      instructor: null,
    };

    setCurrentVenue(newVenue);
    setIsEditMode(false);
    setOpen(true);
  };

  const handleClickOpen = (venue) => {
    setCurrentVenue(venue);
    setOpen(true);
    setIsEditMode(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCurrentVenue((prev) => ({ ...prev, [name]: value }));
  };

  const handleSave = () => {
    axiosInstance
      .put('/venue', currentVenue, {
        headers: {
          'Content-Type': 'application/json',
        },
      })
      .then((response) => {
        setVenues((prev) => prev.map((v) => (v.id === currentVenue.id ? currentVenue : v)));
        setOpen(false);
        showNotification('场地信息更新成功！', 'success');
        checkVenuesAndInstructors(); // 保存后检查
      })
      .catch((error) => {
        console.error('请求出错:', error);
        showNotification('场地信息更新失败！', 'error');
      });
  };

  const handleInsert = () => {
    axiosInstance
      .post('/venue', currentVenue, {
        headers: {
          'Content-Type': 'application/json',
        },
      })
      .then((response) => {
        setOpen(false);
        showNotification('场地添加成功！', 'success');
        checkVenuesAndInstructors(); // 插入后检查
      })
      .catch((error) => {
        console.error('请求出错:', error);
      });
  };

  const handleDelete = () => {
    axiosInstance
      .delete('/venue', { params: { venueId: currentVenue.id } })
      .then((response) => {
        setOpen(false);
        showNotification('场地删除成功！', 'success');
        checkVenuesAndInstructors(); // 删除后检查
      })
      .catch((error) => {
        console.error('请求出错', error);
      });
  };

  const handleReset = () => {
    setTimeZone('');
    setState('');
    setInstructor('');
    setVenues([]);
    setCurrentPage(1);
    showNotification('重置成功！', 'success');
  };

  // 确保 handlePageChange 正确更新 currentPage
  const handlePageChange = (event, value) => {
    setCurrentPage(value);
  };

  const checkVenuesAndInstructors = async () => {
    let updatedCourseSchedules = [];
    for (const venue of venues) {
      if (!venue.instructor) {
        showNotification(`场地 ${venue.id} 没有分配导师！`, 'warning');
      } else {
        const venueSchedule = schedules.filter((schedule) => schedule.venue_id === venue.id);
        const instructorSchedule = schedules.filter((schedule) => schedule.instructor_id === venue.instructor);

        const matchingSchedules = venueSchedule.filter((vs) =>
          instructorSchedule.some(
            (is) =>
              vs.date === is.date &&
              ((is.start_time >= vs.start_time && is.start_time < vs.end_time) ||
                (is.end_time > vs.start_time && is.end_time <= vs.end_time) ||
                (is.start_time <= vs.start_time && is.end_time >= vs.end_time))
          )
        );

        if (matchingSchedules.length > 0) {
          matchingSchedules.forEach((vs) => {
            instructorSchedule.forEach((is) => {
              if (
                vs.date === is.date &&
                ((is.start_time >= vs.start_time && is.start_time < vs.end_time) ||
                  (is.end_time > vs.start_time && is.end_time <= vs.end_time) ||
                  (is.start_time <= vs.start_time && is.end_time >= vs.end_time))
              ) {
                const start_time = is.start_time > vs.start_time ? is.start_time : vs.start_time;
                const end_time = is.end_time < vs.end_time ? is.end_time : vs.end_time;

                updatedCourseSchedules.push({
                  instructor_id: venue.instructor,
                  venue_id: venue.id,
                  date: vs.date,
                  start_time: start_time,
                  end_time: end_time,
                  is_active: 1, // Assuming we want to set the new course schedule as active
                });
              }
            });
          });
        } else {
          showNotification(`导师 ${venue.instructor} 与场地 ${venue.city} 没有匹配的时间表！`, 'warning');
        }
      }
    }

    if (updatedCourseSchedules.length > 0) {
      try {
        await axiosInstance.post('/course', { schedules: updatedCourseSchedules });
        showNotification('课程时间表更新成功！', 'success');
      } catch (error) {
        console.error('更新课程时间表时出错:', error);
        showNotification('更新课程时间表失败！', 'error');
      }
    }
  };

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = venues.slice(indexOfFirstItem, indexOfLastItem);
  const totalPages = Math.ceil(venues.length / itemsPerPage);

  return (
    <SidebarLayout>
      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        <VenueSearchForm
          timeZone={timeZone}
          state={state}
          instructor={instructor}
          setTimeZone={setTimeZone}
          setState={setState}
          setInstructor={setInstructor}
          handleSearch={handleSearch}
          handleReset={handleReset}
        />
        <VenueTable currentItems={currentItems} handleClickOpen={handleClickOpen} />
        <VenuePagination totalPages={totalPages} currentPage={currentPage} handlePageChange={handlePageChange} /> {/* 确保 VenuePagination 组件传递正确的 props */}
        <Button variant="contained" color="primary" onClick={handleAdd} sx={{ mt: 2 }}>
          添加场地
        </Button>
      </Container>
      <VenueDialog
        open={open}
        handleClose={handleClose}
        isEditMode={isEditMode}
        currentVenue={currentVenue}
        handleChange={handleChange}
        handleSave={handleSave}
        handleInsert={handleInsert}
        handleDelete={handleDelete}
        timeZones={timeZones}
      />
    </SidebarLayout>
  );
};

export default VenueMain;
