import React, { useState } from 'react';
import axiosInstance from '../AxiosInstance';
import SidebarLayout from '../SidebarLayout';
import CourseSearchForm from './CourseSearchForm';
import CourseTable from './CourseTable';
import CoursePagination from './CoursePagination';
import { Container, Button } from '@mui/material';
import { useNotification } from '../NotificationContext';

const CourseMain = () => {
  const [timeZone, setTimeZone] = useState('');
  const [state, setState] = useState('');
  const [instructor, setInstructor] = useState('');
  const [courses, setCourses] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10);
  const [currentCourse, setCurrentCourse] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [isEditMode, setIsEditMode] = useState(false);
  const timeZones = ['PST', 'EST', 'CST', 'MST', 'GMT', 'UTC', 'BST', 'CEST'];
  const { showNotification } = useNotification();

  const handleSearch = () => {
    if (isLoading) return;
    setIsLoading(true);

    let params = {
      pageNum: currentPage,
      pageSize: itemsPerPage,
      state,
      city: '',
      instructor: instructor,
      timeZone: timeZone,
    };

    Object.keys(params).forEach(key => {
      if (params[key] === '' || params[key] === null || params[key] === undefined) {
        delete params[key];
      }
    });

    axiosInstance.get('/course', { params })
      .then(response => {
        if (response.data.message === "success") {
          setCourses(response.data.data.items);
          console.log(response.data.data);
          showNotification('Course found successfully!', 'success');
        } else {
          showNotification('Course search failed!', 'error');
        }
      })
      .catch(error => {
        console.error('Error in request:', error);
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const handleAdd = () => {
    const newCourse = {
      courseid: null,
      address: '',
      instructor: '',
      date: '',
      start_time: '',
      end_time: '',
    };

    setCurrentCourse(newCourse);
    setIsEditMode(false);
    // Here you would typically open a modal or a form to add a new course
    // setOpen(true); // Remove this line since we're not using a dialog
  };

  const handleClickOpen = (course) => {
    setCurrentCourse(course);
    // setOpen(true); // Remove this line since we're not using a dialog
    setIsEditMode(true);
  };

  // Remove handleClose function as we're not using a dialog
  // const handleClose = () => {
  //   setOpen(false);
  // };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCurrentCourse(prev => ({ ...prev, [name]: value }));
  };

  const handleSave = () => {
    axiosInstance.put('/course', currentCourse, {
      headers: {
        'Content-Type': 'application/json'
      }
    })
      .then(response => {
        setCourses(prev => prev.map(c => c.courseid === currentCourse.courseid ? currentCourse : c));
        // setOpen(false); // Remove this line since we're not using a dialog
        showNotification('Course updated successfully!', 'success');
      })
      .catch(error => {
        console.error('Error in request:', error);
        showNotification('Course update failed!', 'error');
      });
  };

  const handleInsert = () => {
    axiosInstance.post('/course', currentCourse, {
      headers: {
        'Content-Type': 'application/json'
      }
    })
      .then(response => {
        // setOpen(false); // Remove this line since we're not using a dialog
        showNotification('Course added successfully!', 'success');
      })
      .catch(error => {
        console.error('Error in request:', error);
      });
  };

  const handleDelete = () => {
    axiosInstance.delete('/course', { params: { courseid: currentCourse.courseid } })
      .then(response => {
        // setOpen(false); // Remove this line since we're not using a dialog
        showNotification('Course deleted!', 'success');
      })
      .catch(error => {
        console.error('Error in request', error);
      });
  };

  const handleReset = () => {
    setTimeZone('');
    setState('');
    setInstructor('');
    setCourses([]);
    setCurrentPage(1);
    showNotification('Reset successfully!', 'success');
  };

  const handlePageChange = (event, value) => {
    setCurrentPage(value);
    handleSearch();
  };

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = courses.slice(indexOfFirstItem, indexOfLastItem);
  const totalPages = Math.ceil(courses.length / itemsPerPage);

  return (
    <SidebarLayout>
      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        <CourseSearchForm
          timeZone={timeZone}
          state={state}
          instructor={instructor}
          setTimeZone={setTimeZone}
          setState={setState}
          setInstructor={setInstructor}
          handleSearch={handleSearch}
          handleReset={handleReset}
        />
        <CourseTable  />
        <CoursePagination totalPages={totalPages} currentPage={currentPage} handlePageChange={handlePageChange} />
        <Button variant="contained" color="primary" onClick={handleAdd} sx={{ mt: 2 }}>
          Add Course
        </Button>
      </Container>
    </SidebarLayout>
  );
};

export default CourseMain;
