import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction';

const VenueScheduleCalendar = ({ venueId }) => {
    const [events, setEvents] = useState([]);

    useEffect(() => {
        // Fetch initial events for the venue
        fetchEvents();
    }, [venueId]);

    const fetchEvents = async () => {
        // Replace with your API call
        const response = await fetch(`/api/venues/${venueId}/events`);
        const data = await response.json();
        setEvents(data);
    };

    const handleDateClick = (arg) => {
        const title = prompt('Enter Event Title:');
        if (title) {
            const newEvent = { title, start: arg.date, allDay: arg.allDay };
            setEvents([...events, newEvent]);
            // Save new event to the server
            saveEvent(newEvent);
        }
    };

    const handleEventClick = (clickInfo) => {
        if (window.confirm(`Are you sure you want to delete the event '${clickInfo.event.title}'`)) {
            clickInfo.event.remove();
            // Delete event from the server
            deleteEvent(clickInfo.event.id);
        }
    };

    const saveEvent = async (event) => {
        // Replace with your API call to save event
        await fetch(`/api/venues/${venueId}/events`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(event)
        });
    };

    const deleteEvent = async (eventId) => {
        // Replace with your API call to delete event
        await fetch(`/api/venues/${venueId}/events/${eventId}`, {
            method: 'DELETE'
        });
    };

    return (
        <FullCalendar
            plugins={[dayGridPlugin, interactionPlugin]}
            initialView="dayGridMonth"
            events={events}
            dateClick={handleDateClick}
            eventClick={handleEventClick}
        />
    );
};

export default VenueScheduleCalendar;
